import { createStore } from 'redux';


const initialState = {counter: 0, showCounters: true};
// Reducer function
const counterReducer = (state = initialState, action) => {

  if (action.type === 'increment') {
    //state.counter++; // don't do this...  objects are reference values (this is mutating the existing state)
    // instead, return NEW STATE OBJECT by overriding the existing state
    return {
      counters: state.counter + 1,
      // don't need this but redux won't merge our state changes so we have to be explicit about it
      showCounters: state.showCounter 
    }
  }

  if (action.type === 'increase') {
    return {
      // increase counter by a 'certain' amount (aka payload)
      counters: state.counters + action.amount,
      showCounters: state.showCounters
    };
  }

  if (action.type === 'decrement') {
    return {
      counters: state.counters - 1,
      showCounters: state.showCounters
    }
  }

  if (action.type === 'toggle') {
    return {
      counters: state.counters, // keep existing state
      showCounters: !state.showCounters // toggle counter
    };
  }

  return state;
};

// Store
const store = createStore(counterReducer);


// Export store to be used in index.js - wrap the entire app (or apps u want to have access to the store) with it
export default store;
